    /// The fee can change over time, so this method should be called before each request.
    function getFeeV2() external view returns (uint128 feeAmount);

    /// @notice Get the fee charged by the default provider for a specific gas limit
    /// @param gasLimit The gas limit for the callback function
    /// @return feeAmount The fee amount in wei
    /// @dev This method returns the fee required to make a request using the default provider with
    /// the specified gas limit. This fee should be passed as msg.value when calling requestV2(gasLimit).
    /// The fee can change over time, so this method should be called before each request.
    function getFeeV2(
        uint32 gasLimit
    ) external view returns (uint128 feeAmount);

    /// @notice Get the fee charged by a specific provider for a request with a given gas limit
    /// @param provider The address of the provider to query
    /// @param gasLimit The gas limit for the callback function
    /// @return feeAmount The fee amount in wei
    /// @dev This method returns the fee required to make a request using the specified provider with
    /// the given gas limit. This fee should be passed as msg.value when calling requestV2(provider, gasLimit)
    /// or requestV2(provider, userRandomNumber, gasLimit). The fee can change over time, so this method
    /// should be called before each request.
    function getFeeV2(
        address provider,
        uint32 gasLimit
    ) external view returns (uint128 feeAmount);
}

contracts/COPYCATLottery.sol.content:
// SPDX-License-Identifier: Apache 2
pragma solidity ^0.8.0;

// Import the entropy SDK in order to interact with the entropy contracts
import "@pythnetwork/entropy-sdk-solidity/IEntropyV2.sol";
import "@pythnetwork/entropy-sdk-solidity/IEntropyConsumer.sol";
// Import the EntropyStructsV2 contract to get the ProviderInfo struct
import "@pythnetwork/entropy-sdk-solidity/EntropyStructsV2.sol";

library LotteryErrors {
    error InsufficientFee();
    error LotteryNotActive();
    error LotteryAlreadyEnded();
    error NoParticipants();
    error NotOwner();
    error InvalidLotteryId();
    error AlreadyParticipated();
    error LotteryInProgress();
}

/// COPYCAT Lottery contract using Pyth Entropy for fair random number generation.
/// Users can participate in lottery rounds by paying an entry fee.
/// The lottery uses Pyth Entropy to ensure fair and verifiable randomness.
/// Winners are selected based on the random number generated by the entropy provider.
contract COPYCATLottery is IEntropyConsumer {
    // Events
    event LotteryCreated(uint256 indexed lotteryId, uint256 entryFee, uint256 maxParticipants, uint256 duration);
    event LotteryJoined(uint256 indexed lotteryId, address indexed participant, uint256 participantCount);
    event LotteryEnded(uint256 indexed lotteryId, address indexed winner, uint256 prizeAmount, uint256 sequenceNumber);
    event RandomNumberRequested(uint256 indexed lotteryId, uint64 sequenceNumber);
    event PrizeClaimed(uint256 indexed lotteryId, address indexed winner, uint256 amount);

    // Lottery structure
    struct Lottery {
        uint256 lotteryId;
        uint256 entryFee;
        uint256 maxParticipants;
        uint256 startTime;
        uint256 endTime;
        uint256 prizePool;
        address[] participants;
        address winner;
        bool isActive;
        bool isEnded;
        uint64 sequenceNumber;
        bool randomNumberReceived;
    }

    // State variables
    IEntropyV2 private entropy;
    address private entropyProvider;
    address public owner;
    uint256 public nextLotteryId;
    uint256 public totalLotteries;
    uint256 public totalPrizesDistributed;

    // Lottery mappings
    mapping(uint256 => Lottery) public lotteries;
    mapping(uint256 => mapping(address => bool)) public hasParticipated;
    mapping(address => uint256) public userWinnings;
    mapping(address => uint256) public userParticipations;

    // Constants
    uint256 public constant MIN_ENTRY_FEE = 0.001 ether;
    uint256 public constant MAX_ENTRY_FEE = 1 ether;
    uint256 public constant MIN_PARTICIPANTS = 2;
    uint256 public constant MAX_PARTICIPANTS = 1000;
    uint256 public constant MIN_DURATION = 1 hours;
    uint256 public constant MAX_DURATION = 7 days;

    constructor(address _entropy, address _entropyProvider) {
        entropy = IEntropyV2(_entropy);
        entropyProvider = _entropyProvider;
        owner = msg.sender;
    }

    modifier onlyOwner() {
        if (msg.sender != owner) {
            revert LotteryErrors.NotOwner();
        }
        _;
    }

    modifier lotteryExists(uint256 _lotteryId) {
        if (_lotteryId >= nextLotteryId) {
            revert LotteryErrors.InvalidLotteryId();
        }
        _;
    }

    /// Create a new lottery
    function createLottery(
        uint256 _entryFee,
        uint256 _maxParticipants,
        uint256 _duration
    ) external onlyOwner returns (uint256) {
        if (_entryFee < MIN_ENTRY_FEE || _entryFee > MAX_ENTRY_FEE) {
            revert LotteryErrors.InsufficientFee();
        }
        if (_maxParticipants < MIN_PARTICIPANTS || _maxParticipants > MAX_PARTICIPANTS) {
            revert LotteryErrors.InvalidLotteryId();
        }
        if (_duration < MIN_DURATION || _duration > MAX_DURATION) {
            revert LotteryErrors.InvalidLotteryId();
        }

        uint256 lotteryId = nextLotteryId++;
        totalLotteries++;

        Lottery storage lottery = lotteries[lotteryId];
        lottery.lotteryId = lotteryId;
        lottery.entryFee = _entryFee;
        lottery.maxParticipants = _maxParticipants;
        lottery.startTime = block.timestamp;
        lottery.endTime = block.timestamp + _duration;
        lottery.isActive = true;
        lottery.isEnded = false;

        emit LotteryCreated(lotteryId, _entryFee, _maxParticipants, _duration);
        return lotteryId;
    }

    /// Join a lottery by paying the entry fee
    function joinLottery(uint256 _lotteryId) external payable lotteryExists(_lotteryId) {
        Lottery storage lottery = lotteries[_lotteryId];

        if (!lottery.isActive) {
            revert LotteryErrors.LotteryNotActive();
        }
        if (block.timestamp >= lottery.endTime) {
            revert LotteryErrors.LotteryAlreadyEnded();
        }
        if (hasParticipated[_lotteryId][msg.sender]) {
            revert LotteryErrors.AlreadyParticipated();
        }
        if (lottery.participants.length >= lottery.maxParticipants) {
            revert LotteryErrors.LotteryInProgress();
        }
        if (msg.value != lottery.entryFee) {
            revert LotteryErrors.InsufficientFee();
        }

        lottery.participants.push(msg.sender);
        lottery.prizePool += msg.value;
        hasParticipated[_lotteryId][msg.sender] = true;
        userParticipations[msg.sender]++;

        emit LotteryJoined(_lotteryId, msg.sender, lottery.participants.length);

        // Auto-end lottery if max participants reached
        if (lottery.participants.length >= lottery.maxParticipants) {
            _endLottery(_lotteryId);
        }
    }

    /// End a lottery manually (only if time has passed or max participants reached)
    function endLottery(uint256 _lotteryId) external lotteryExists(_lotteryId) {
        Lottery storage lottery = lotteries[_lotteryId];

        if (!lottery.isActive) {
            revert LotteryErrors.LotteryNotActive();
        }
        if (block.timestamp < lottery.endTime && lottery.participants.length < lottery.maxParticipants) {
            revert LotteryErrors.LotteryInProgress();
        }

        _endLottery(_lotteryId);
    }

    /// Internal function to end a lottery and request random number
    function _endLottery(uint256 _lotteryId) internal {
        Lottery storage lottery = lotteries[_lotteryId];

        if (lottery.participants.length == 0) {
            revert LotteryErrors.NoParticipants();
        }

        lottery.isActive = false;

        // If only one participant, they win by default
        if (lottery.participants.length == 1) {
            lottery.winner = lottery.participants[0];
            lottery.isEnded = true;
            userWinnings[lottery.winner] += lottery.prizePool;
            totalPrizesDistributed += lottery.prizePool;

            emit LotteryEnded(_lotteryId, lottery.winner, lottery.prizePool, 0);
            return;
        }

        // Request random number from Pyth Entropy
        uint256 fee = entropy.getFeeV2();
        uint64 sequenceNumber = entropy.requestV2{value: fee}();

        lottery.sequenceNumber = sequenceNumber;
        emit RandomNumberRequested(_lotteryId, sequenceNumber);
    }

    /// Callback function called by Pyth Entropy when random number is generated
    function entropyCallback(
        uint64 sequenceNumber,
        address,
        bytes32 randomNumber
    ) internal override {
        // Find the lottery with this sequence number
        for (uint256 i = 0; i < nextLotteryId; i++) {
            Lottery storage lottery = lotteries[i];
            if (lottery.sequenceNumber == sequenceNumber && !lottery.randomNumberReceived) {
                lottery.randomNumberReceived = true;
                _selectWinner(i, randomNumber);
                break;
            }
        }
    }

    /// Select winner based on random number
    function _selectWinner(uint256 _lotteryId, bytes32 _randomNumber) internal {
        Lottery storage lottery = lotteries[_lotteryId];

        uint256 randomIndex = uint256(_randomNumber) % lottery.participants.length;
        lottery.winner = lottery.participants[randomIndex];
        lottery.isEnded = true;

        userWinnings[lottery.winner] += lottery.prizePool;
        totalPrizesDistributed += lottery.prizePool;

        emit LotteryEnded(_lotteryId, lottery.winner, lottery.prizePool, lottery.sequenceNumber);
    }

    /// Claim prize (winner can claim their winnings)
    function claimPrize(uint256 _lotteryId) external lotteryExists(_lotteryId) {
        Lottery storage lottery = lotteries[_lotteryId];

        if (!lottery.isEnded) {
            revert LotteryErrors.LotteryInProgress();
        }
        if (lottery.winner != msg.sender) {
            revert LotteryErrors.NotOwner();
        }
        if (lottery.prizePool == 0) {
            revert LotteryErrors.InsufficientFee();
        }

        uint256 prizeAmount = lottery.prizePool;
        lottery.prizePool = 0;

        (bool success, ) = payable(msg.sender).call{value: prizeAmount}("");
        require(success, "Transfer failed");

        emit PrizeClaimed(_lotteryId, msg.sender, prizeAmount);
    }

    /// Get lottery information
    function getLottery(uint256 _lotteryId) external view lotteryExists(_lotteryId) returns (
        uint256 lotteryId,
        uint256 entryFee,
        uint256 maxParticipants,
        uint256 startTime,
        uint256 endTime,
        uint256 prizePool,
        uint256 participantCount,
        address winner,
        bool isActive,
        bool isEnded,
        uint64 sequenceNumber
    ) {
        Lottery storage lottery = lotteries[_lotteryId];
        return (
            lottery.lotteryId,
            lottery.entryFee,
            lottery.maxParticipants,
            lottery.startTime,
            lottery.endTime,
            lottery.prizePool,
            lottery.participants.length,
            lottery.winner,
            lottery.isActive,
            lottery.isEnded,
            lottery.sequenceNumber
        );
    }

    /// Get lottery participants
    function getLotteryParticipants(uint256 _lotteryId) external view lotteryExists(_lotteryId) returns (address[] memory) {
        return lotteries[_lotteryId].participants;
    }

    /// Get user statistics
    function getUserStats(address _user) external view returns (
        uint256 winnings,
        uint256 participations
    ) {
        return (userWinnings[_user], userParticipations[_user]);
    }

    /// Get contract statistics
    function getContractStats() external view returns (
        uint256 totalLotteries_,
        uint256 totalPrizesDistributed_,
        uint256 currentLotteryId
    ) {
        return (totalLotteries, totalPrizesDistributed, nextLotteryId);
    }

    /// Get entropy fee
    function getEntropyFee() public view returns (uint256) {
        return entropy.getFeeV2();
    }

    /// Required by IEntropyConsumer interface
    function getEntropy() internal view override returns (address) {
        return address(entropy);
    }

    /// Emergency functions (only owner)
    function emergencyWithdraw() external onlyOwner {
        payable(owner).transfer(address(this).balance);
    }

    function setEntropyProvider(address _newProvider) external onlyOwner {
        entropyProvider = _newProvider;
    }

    receive() external payable {}
}

contracts/ProfitableSaucerSwap.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Minimal IERC20 interface
interface IERC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
}

// SaucerSwap V2 Router interface
interface ISwapRouter {
    struct ExactInputParams {
        bytes path;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
    }

    struct ExactOutputParams {
        bytes path;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
    }

    function exactInput(ExactInputParams calldata params) external payable returns (uint256 amountOut);
    function exactOutput(ExactOutputParams calldata params) external payable returns (uint256 amountIn);
    function multicall(bytes[] calldata data) external payable returns (bytes[] memory results);
    function refundETH() external payable;
    function unwrapWETH9(uint256 amountMinimum, address recipient) external payable;
}

// QuoterV2 interface for getting quotes
interface IQuoterV2 {
    function quoteExactInput(bytes memory path, uint256 amountIn)
        external
        returns (
            uint256 amountOut,
            uint160[] memory sqrtPriceX96AfterList,
            uint32[] memory initializedTicksCrossedList,
            uint256 gasEstimate
        );

    function quoteExactOutput(bytes memory path, uint256 amountOut)
        external
        returns (
            uint256 amountIn,
            uint160[] memory sqrtPriceX96AfterList,
            uint32[] memory initializedTicksCrossedList,
            uint256 gasEstimate
        );
}

// Interface for the lottery contract
interface ISwapLottery {
    function enterUser(address user, uint256 swapProfit) external;
}

/**
 * @title ProfitableSaucerSwap
 * @dev A contract to handle SaucerSwap trading with profit tracking and lottery integration
 */
contract ProfitableSaucerSwap {
    ISwapRouter public immutable swapRouter;
    IQuoterV2 public immutable quoter;
    ISwapLottery public lotteryContract;

    // WHBAR address (Wrapped HBAR)
    address public immutable WHBAR;

    // Owner address
    address public owner;

    // Lottery pool address
    address public lotteryPool;

    // Reentrancy guard
    bool private locked;

    // User swap history for profit calculation
    struct SwapRecord {
        address tokenIn;
        address tokenOut;
        uint256 amountIn;
        uint256 amountOut;
        uint256 timestamp;
        bool isRoundTripStart; // true if this is the first swap in a potential round trip
    }

    mapping(address => SwapRecord[]) public userSwapHistory;
    mapping(address => uint256) public userTotalProfits;
    mapping(address => uint256) public userLotteryContributions;

    // Events
    event SwapExecuted(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 profit,
        uint256 lotteryContribution
    );

    event RoundTripCompleted(
        address indexed user,
        uint256 profit,
        uint256 lotteryContribution
    );

    event LotteryContractUpdated(address indexed newLotteryContract);
    event LotteryPoolUpdated(address indexed newLotteryPool);

    event EmergencyWithdraw(
        address indexed token,
        uint256 amount,
        address indexed to
    );

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    modifier nonReentrant() {
        require(!locked, "Reentrant call");
        locked = true;
        _;
        locked = false;
    }

    constructor(
        address _swapRouter,
        address _quoter,
        address _whbar,
        address _lotteryPool
    ) {
        swapRouter = ISwapRouter(_swapRouter);
        quoter = IQuoterV2(_quoter);
        WHBAR = _whbar;
        lotteryPool = _lotteryPool;
        owner = msg.sender;
    }

    /**
     * @dev Set the lottery contract address
     */
    function setLotteryContract(address _lotteryContract) external onlyOwner {
        lotteryContract = ISwapLottery(_lotteryContract);
        emit LotteryContractUpdated(_lotteryContract);
    }

    /**
     * @dev Set the lottery pool address
     */
    function setLotteryPool(address _lotteryPool) external onlyOwner {
        lotteryPool = _lotteryPool;
        emit LotteryPoolUpdated(_lotteryPool);
    }

    /**
     * @dev Internal function to calculate profit and handle lottery contributions
     */
    function _handleProfitAndLottery(
        address user,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut
    ) internal returns (uint256 profit, uint256 lotteryContribution) {
        SwapRecord[] storage history = userSwapHistory[user];

        // Check for round-trip completion
        bool isRoundTripComplete = false;
        uint256 originalAmount = 0;

        if (history.length > 0) {
            SwapRecord storage lastSwap = history[history.length - 1];

            // Check if this completes a round trip (swap back to original token)
            if (lastSwap.isRoundTripStart &&
                lastSwap.tokenOut == tokenIn &&
                lastSwap.tokenIn == tokenOut) {

                isRoundTripComplete = true;
                originalAmount = lastSwap.amountIn;

                // Calculate profit (if we got more than we started with)
                if (amountOut > originalAmount) {
                    profit = amountOut - originalAmount;

                    // Calculate 10% charge on profit
                    uint256 totalCharge = (profit * 1000) / 10000; // 10%

                    // 8% goes to lottery pool
                    lotteryContribution = (profit * 800) / 10000; // 8%

                    // Transfer lottery contribution to lottery pool
                    if (lotteryContribution > 0) {
                        if (tokenOut == WHBAR) {
                            // If profit is in HBAR, transfer directly
                            payable(lotteryPool).transfer(lotteryContribution);
                        } else {
                            // If profit is in tokens, transfer tokens
                            IERC20(tokenOut).transfer(lotteryPool, lotteryContribution);
                        }

                        userLotteryContributions[user] += lotteryContribution;

                        // Enter user in lottery if lottery contract is set
                        if (address(lotteryContract) != address(0)) {
                            lotteryContract.enterUser(user, profit);
                        }
                    }

                    userTotalProfits[user] += profit;

                    emit RoundTripCompleted(user, profit, lotteryContribution);
                }

                // Mark last swap as no longer round trip start
                lastSwap.isRoundTripStart = false;
            }
        }

        // Record current swap
        SwapRecord memory newRecord = SwapRecord({
            tokenIn: tokenIn,
            tokenOut: tokenOut,
            amountIn: amountIn,
            amountOut: amountOut,
            timestamp: block.timestamp,
            isRoundTripStart: !isRoundTripComplete // This could be start of new round trip
        });

        history.push(newRecord);
    }

    /**
     * @dev Swap exact HBAR for tokens
     */
    function swapExactHBARForTokens(
        address tokenOut,
        uint24 fee,
        uint256 amountOutMinimum,
        uint256 deadline
    ) external payable nonReentrant returns (uint256 amountOut) {
        require(msg.value > 0, "Must send HBAR");
        require(tokenOut != address(0), "Invalid token address");
        require(deadline >= block.timestamp, "Deadline expired");

        // Create the swap path: WHBAR -> tokenOut
        bytes memory path = abi.encodePacked(WHBAR, fee, tokenOut);

        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: path,
            recipient: address(this), // Receive tokens to this contract for profit calculation
            deadline: deadline,
            amountIn: msg.value,
            amountOutMinimum: amountOutMinimum
        });

        // Execute the swap with HBAR value
        amountOut = swapRouter.exactInput{value: msg.value}(params);

        // Handle profit calculation and lottery
        (uint256 profit, uint256 lotteryContribution) = _handleProfitAndLottery(
            msg.sender,
            WHBAR,
            tokenOut,
            msg.value,
            amountOut
        );

        // Transfer tokens to user (minus any lottery contribution)
        uint256 userAmount = amountOut - lotteryContribution;
        if (userAmount > 0) {
            IERC20(tokenOut).transfer(msg.sender, userAmount);
        }

        emit SwapExecuted(msg.sender, WHBAR, tokenOut, msg.value, amountOut, profit, lotteryContribution);
        return amountOut;
    }

    /**
     * @dev Swap exact tokens for HBAR
     */
    function swapExactTokensForHBAR(
        address tokenIn,
        uint24 fee,
        uint256 amountIn,
        uint256 amountOutMinimum,
        uint256 deadline
    ) external nonReentrant returns (uint256 amountOut) {
        require(tokenIn != address(0), "Invalid token address");
        require(amountIn > 0, "Amount in must be > 0");
        require(deadline >= block.timestamp, "Deadline expired");

        // Transfer tokens from sender to this contract
        IERC20(tokenIn).transferFrom(msg.sender, address(this), amountIn);

        // Approve the router to spend the tokens
        IERC20(tokenIn).approve(address(swapRouter), amountIn);

        // Create the swap path: tokenIn -> WHBAR
        bytes memory path = abi.encodePacked(tokenIn, fee, WHBAR);

        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: path,
            recipient: address(this), // Receive WHBAR to this contract first
            deadline: deadline,
            amountIn: amountIn,
            amountOutMinimum: amountOutMinimum
        });

        // Execute the swap to get WHBAR
        amountOut = swapRouter.exactInput(params);

        // Unwrap WHBAR to HBAR
        bytes[] memory multicallData = new bytes[](2);
        multicallData[0] = abi.encodeWithSelector(
            ISwapRouter.unwrapWETH9.selector,
            amountOut,
            address(this)
        );
        multicallData[1] = abi.encodeWithSelector(ISwapRouter.refundETH.selector);

        swapRouter.multicall(multicallData);

        // Handle profit calculation and lottery
        (uint256 profit, uint256 lotteryContribution) = _handleProfitAndLottery(
            msg.sender,
            tokenIn,
            WHBAR,
            amountIn,
            amountOut
        );

        // Send HBAR to user (minus any lottery contribution)
        uint256 userAmount = amountOut - lotteryContribution;
        if (userAmount > 0) {
            payable(msg.sender).transfer(userAmount);
        }

        emit SwapExecuted(msg.sender, tokenIn, WHBAR, amountIn, amountOut, profit, lotteryContribution);
        return amountOut;
    }

    /**
     * @dev Swap exact input tokens for output tokens
     */
    function swapExactTokensForTokens(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn,
        uint256 amountOutMinimum,
        uint256 deadline
    ) external nonReentrant returns (uint256 amountOut) {
        require(tokenIn != address(0) && tokenOut != address(0), "Invalid token address");
        require(tokenIn != tokenOut, "Cannot swap same token");
        require(amountIn > 0, "Amount in must be > 0");
        require(deadline >= block.timestamp, "Deadline expired");

        // Transfer tokens from sender to this contract
        IERC20(tokenIn).transferFrom(msg.sender, address(this), amountIn);

        // Approve the router to spend the tokens
        IERC20(tokenIn).approve(address(swapRouter), amountIn);

        // Create the swap path: tokenIn -> tokenOut
        bytes memory path = abi.encodePacked(tokenIn, fee, tokenOut);

        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: path,
            recipient: address(this), // Receive tokens to this contract for profit calculation
            deadline: deadline,
            amountIn: amountIn,
            amountOutMinimum: amountOutMinimum
        });

        // Execute the swap
        amountOut = swapRouter.exactInput(params);

        // Handle profit calculation and lottery
        (uint256 profit, uint256 lotteryContribution) = _handleProfitAndLottery(
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            amountOut
        );

        // Transfer tokens to user (minus any lottery contribution)
        uint256 userAmount = amountOut - lotteryContribution;
        if (userAmount > 0) {
            IERC20(tokenOut).transfer(msg.sender, userAmount);
        }

        emit SwapExecuted(msg.sender, tokenIn, tokenOut, amountIn, amountOut, profit, lotteryContribution);
        return amountOut;
    }

    /**
     * @dev Get a quote for exact input swap
     */
    function getQuoteExactInput(bytes memory path, uint256 amountIn)
        external
        returns (
            uint256 amountOut,
            uint160[] memory sqrtPriceX96AfterList,
            uint32[] memory initializedTicksCrossedList,
            uint256 gasEstimate
        )
    {
        return quoter.quoteExactInput(path, amountIn);
    }

    /**
     * @dev Get user's swap history length
     */
    function getUserSwapHistoryLength(address user) external view returns (uint256) {
        return userSwapHistory[user].length;
    }

    /**
     * @dev Get user's swap record at index
     */
    function getUserSwapRecord(address user, uint256 index) external view returns (
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 timestamp,
        bool isRoundTripStart
    ) {
        require(index < userSwapHistory[user].length, "Index out of bounds");
        SwapRecord memory record = userSwapHistory[user][index];
        return (
            record.tokenIn,
            record.tokenOut,
            record.amountIn,
            record.amountOut,
            record.timestamp,
            record.isRoundTripStart
        );
    }

    /**
     * @dev Get user statistics
     */
    function getUserStats(address user) external view returns (
        uint256 totalProfits,
        uint256 lotteryContributions,
        uint256 totalSwaps
    ) {
        return (
            userTotalProfits[user],
            userLotteryContributions[user],
            userSwapHistory[user].length
        );
    }

    /**
     * @dev Emergency function to withdraw tokens
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        require(amount > 0, "Amount must be > 0");

        if (token == address(0)) {
            require(address(this).balance >= amount, "Insufficient HBAR balance");
            payable(owner).transfer(amount);
        } else {
            require(IERC20(token).balanceOf(address(this)) >= amount, "Insufficient token balance");
            bool success = IERC20(token).transfer(owner, amount);
            require(success, "Token transfer failed");
        }

        emit EmergencyWithdraw(token, amount, owner);
    }

    /**
     * @dev Transfer ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid new owner");
        require(newOwner != owner, "New owner must be different");

        address previousOwner = owner;
        owner = newOwner;

        emit OwnershipTransferred(previousOwner, newOwner);
    }

    /**
     * @dev Get contract's balance of a specific token
     */
    function getTokenBalance(address token) external view returns (uint256) {
        if (token == address(0)) {
            return address(this).balance;
        }
        return IERC20(token).balanceOf(address(this));
    }

    /**
     * @dev Get current contract configuration
     */
    function getContractInfo() external view returns (
        address _swapRouter,
        address _quoter,
        address _whbar,
        address _owner,
        address _lotteryPool,
        address _lotteryContract
    ) {
        return (
            address(swapRouter),
            address(quoter),
            WHBAR,
            owner,
            lotteryPool,
            address(lotteryContract)
        );
    }

    /**
     * @dev Allow the contract to receive HBAR
     */
    receive() external payable {}
}

contracts/SwapLottery.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Import the entropy SDK in order to interact with the entropy contracts
import "@pythnetwork/entropy-sdk-solidity/IEntropyV2.sol";
import "@pythnetwork/entropy-sdk-solidity/IEntropyConsumer.sol";

library SwapLotteryErrors {
    error InsufficientFunds();
    error LotteryNotActive();
    error LotteryAlreadyEnded();
    error NoParticipants();
    error NotOwner();
    error NotAuthorizedContract();
    error InvalidWinnerNumber();
    error AlreadyParticipated();
    error LotteryInProgress();
    error InvalidLotteryDuration();
    error RandomNumberNotReady();
}

/**
 * @title SwapLottery
 * @dev A lottery contract that uses Pyth Entropy for random number generation.
 * Users are entered into the lottery when they complete profitable swaps.
 * The lottery uses Pyth Entropy to generate random numbers for fair winner selection.
 */
contract SwapLottery is IEntropyConsumer {

    // Events
    event LotteryStarted(uint256 indexed lotteryId, uint256 duration, uint256 timestamp);
    event UserEntered(uint256 indexed lotteryId, address indexed user, uint256 ticketNumber, uint256 swapProfit);
    event RandomNumberRequested(uint256 indexed lotteryId, uint64 sequenceNumber);
    event WinnerSelected(uint256 indexed lotteryId, address indexed winner, uint256 winningNumber, uint256 prizeAmount);
    event LotteryEnded(uint256 indexed lotteryId, uint256 totalPrize, uint256 totalParticipants);
    event PrizeClaimed(uint256 indexed lotteryId, address indexed winner, uint256 amount);

    // Lottery structure
    struct Lottery {
        uint256 lotteryId;
        uint256 startTime;
        uint256 endTime;
        uint256 prizePool;
        address[] participants;
        mapping(address => uint256[]) userTickets; // User's assigned random numbers
        mapping(address => bool) hasParticipated;
        address winner;
        uint256 winningNumber;
        bool isActive;
        bool isEnded;
        uint64 sequenceNumber;
        bool randomNumberReceived;
        bool prizeClaimed;
    }

    // State variables
    IEntropyV2 private entropy;
    address private entropyProvider;
    address public owner;
    address public swapContract; // The ProfitableSaucerSwap contract

    uint256 public currentLotteryId;
    mapping(uint256 => Lottery) public lotteries;

    // Lottery settings
    uint256 public lotteryDuration; // Duration of each lottery round in seconds
    uint256 public minParticipants; // Minimum participants to draw winner

    // Statistics
    uint256 public totalLotteries;
    uint256 public totalPrizesDistributed;
    mapping(address => uint256) public userTotalWinnings;
    mapping(address => uint256) public userParticipationCount;

    constructor(
        address _entropy,
        address _entropyProvider,
        uint256 _lotteryDuration,
        uint256 _minParticipants
    ) {
        entropy = IEntropyV2(_entropy);
        entropyProvider = _entropyProvider;
        owner = msg.sender;
        lotteryDuration = _lotteryDuration;
        minParticipants = _minParticipants;

        // Start the first lottery
        _startNewLottery();
    }

    modifier onlyOwner() {
        if (msg.sender != owner) {
            revert SwapLotteryErrors.NotOwner();
        }
        _;
    }

    modifier onlySwapContract() {
        if (msg.sender != swapContract) {
            revert SwapLotteryErrors.NotAuthorizedContract();
        }
        _;
    }

    modifier lotteryExists(uint256 _lotteryId) {
        if (_lotteryId > currentLotteryId) {
            revert SwapLotteryErrors.InvalidWinnerNumber();
        }
        _;
    }

    /**
     * @dev Set the authorized swap contract
     */
    function setSwapContract(address _swapContract) external onlyOwner {
        swapContract = _swapContract;
    }

    /**
     * @dev Start a new lottery round
     */
    function _startNewLottery() internal {
        currentLotteryId++;
        totalLotteries++;

        Lottery storage lottery = lotteries[currentLotteryId];
        lottery.lotteryId = currentLotteryId;
        lottery.startTime = block.timestamp;
        lottery.endTime = block.timestamp + lotteryDuration;
        lottery.isActive = true;
        lottery.isEnded = false;
        lottery.randomNumberReceived = false;
        lottery.prizeClaimed = false;

        emit LotteryStarted(currentLotteryId, lotteryDuration, block.timestamp);
    }

    /**
     * @dev Enter a user into the current lottery (called by swap contract)
     */
    function enterUser(address user, uint256 swapProfit) external onlySwapContract {
        Lottery storage lottery = lotteries[currentLotteryId];

        if (!lottery.isActive) {
            revert SwapLotteryErrors.LotteryNotActive();
        }

        if (block.timestamp >= lottery.endTime) {
            // End current lottery and start new one
            _endCurrentLottery();
            _startNewLottery();
            lottery = lotteries[currentLotteryId];
        }

        // Generate a random ticket number for the user using Pyth entropy
        uint256 ticketNumber = _generateTicketNumber(user, swapProfit);

        // Add user to lottery if not already participated
        if (!lottery.hasParticipated[user]) {
            lottery.participants.push(user);
            lottery.hasParticipated[user] = true;
            userParticipationCount[user]++;
        }

        // Add ticket number to user's tickets
        lottery.userTickets[user].push(ticketNumber);

        emit UserEntered(currentLotteryId, user, ticketNumber, swapProfit);
    }

    /**
     * @dev Generate a ticket number for a user based on their swap profit and other factors
     */
    function _generateTicketNumber(address user, uint256 swapProfit) internal view returns (uint256) {
        // Create a pseudo-random number based on multiple factors
        // This will be used until Pyth entropy provides the final random number
        bytes32 hash = keccak256(abi.encodePacked(
            block.timestamp,
            block.difficulty,
            user,
            swapProfit,
            currentLotteryId,
            lotteries[currentLotteryId].participants.length
        ));

        // Convert to number between 1 and 1000000
        return (uint256(hash) % 1000000) + 1;
    }

    /**
     * @dev End the current lottery manually (anyone can call if time expired)
     */
    function endCurrentLottery() external {
        Lottery storage lottery = lotteries[currentLotteryId];

        if (!lottery.isActive) {
            revert SwapLotteryErrors.LotteryNotActive();
        }

        if (block.timestamp < lottery.endTime) {
            revert SwapLotteryErrors.LotteryInProgress();
        }

        _endCurrentLottery();
    }

    /**
     * @dev Internal function to end the current lottery
     */
    function _endCurrentLottery() internal {
        Lottery storage lottery = lotteries[currentLotteryId];

        if (lottery.participants.length == 0) {
            lottery.isActive = false;
            lottery.isEnded = true;
            emit LotteryEnded(currentLotteryId, lottery.prizePool, 0);
            return;
        }

        lottery.isActive = false;

        if (lottery.participants.length < minParticipants) {
            // Not enough participants, roll over prize to next lottery
            lottery.isEnded = true;
            emit LotteryEnded(currentLotteryId, lottery.prizePool, lottery.participants.length);
            return;
        }

        // Request random number from Pyth Entropy
        uint256 fee = entropy.getFeeV2();
        if (address(this).balance >= fee) {
            uint64 sequenceNumber = entropy.requestV2{value: fee}();
            lottery.sequenceNumber = sequenceNumber;
            emit RandomNumberRequested(currentLotteryId, sequenceNumber);
        } else {
            // Fallback: use block-based randomness if we can't pay entropy fee
            _selectWinnerWithFallback(currentLotteryId);
        }
    }

    /**
     * @dev Select winner using a specific winning number
     */
    function selectWinner(uint256 _lotteryId, uint256 _winningNumber) external lotteryExists(_lotteryId) {
        Lottery storage lottery = lotteries[_lotteryId];

        if (lottery.isEnded) {
            revert SwapLotteryErrors.LotteryAlreadyEnded();
        }

        if (lottery.isActive) {
            revert SwapLotteryErrors.LotteryInProgress();
        }

        if (_winningNumber == 0 || _winningNumber > 1000000) {
            revert SwapLotteryErrors.InvalidWinnerNumber();
        }

        _selectWinnerByNumber(_lotteryId, _winningNumber);
    }

    /**
     * @dev Callback function called by Pyth Entropy when random number is generated
     */
    function entropyCallback(
        uint64 sequenceNumber,
        address,
        bytes32 randomNumber
    ) internal override {
        // Find the lottery with this sequence number
        for (uint256 i = 1; i <= currentLotteryId; i++) {
            Lottery storage lottery = lotteries[i];
            if (lottery.sequenceNumber == sequenceNumber && !lottery.randomNumberReceived) {
                lottery.randomNumberReceived = true;

                // Convert random number to winning number (1-1000000)
                uint256 winningNumber = (uint256(randomNumber) % 1000000) + 1;
                _selectWinnerByNumber(i, winningNumber);
                break;
            }
        }
    }

    /**
     * @dev Select winner by finding closest ticket number to winning number
     */
    function _selectWinnerByNumber(uint256 _lotteryId, uint256 _winningNumber) internal {
        Lottery storage lottery = lotteries[_lotteryId];

        if (lottery.participants.length == 0) {
            revert SwapLotteryErrors.NoParticipants();
        }

        address closestWinner = address(0);
        uint256 closestDifference = type(uint256).max;

        // Find the participant with the ticket number closest to winning number
        for (uint256 i = 0; i < lottery.participants.length; i++) {
            address participant = lottery.participants[i];
            uint256[] storage userTickets = lottery.userTickets[participant];

            for (uint256 j = 0; j < userTickets.length; j++) {
                uint256 ticketNumber = userTickets[j];
                uint256 difference = ticketNumber > _winningNumber ?
                    ticketNumber - _winningNumber :
                    _winningNumber - ticketNumber;

                if (difference < closestDifference) {
                    closestDifference = difference;
                    closestWinner = participant;
                }
            }
        }

        lottery.winner = closestWinner;
        lottery.winningNumber = _winningNumber;
        lottery.isEnded = true;

        if (closestWinner != address(0)) {
            userTotalWinnings[closestWinner] += lottery.prizePool;
            totalPrizesDistributed += lottery.prizePool;
        }

        emit WinnerSelected(_lotteryId, closestWinner, _winningNumber, lottery.prizePool);
        emit LotteryEnded(_lotteryId, lottery.prizePool, lottery.participants.length);
    }

    /**
     * @dev Fallback winner selection using block-based randomness
     */
    function _selectWinnerWithFallback(uint256 _lotteryId) internal {
        bytes32 blockHash = keccak256(abi.encodePacked(
            block.timestamp,
            block.difficulty,
            _lotteryId
        ));
        uint256 winningNumber = (uint256(blockHash) % 1000000) + 1;
        _selectWinnerByNumber(_lotteryId, winningNumber);
    }

    /**
     * @dev Claim prize for a lottery (winner only)
     */
    function claimPrize(uint256 _lotteryId) external lotteryExists(_lotteryId) {
        Lottery storage lottery = lotteries[_lotteryId];

        if (!lottery.isEnded) {
            revert SwapLotteryErrors.LotteryInProgress();
        }

        if (lottery.winner != msg.sender) {
            revert SwapLotteryErrors.NotOwner();
        }

        if (lottery.prizeClaimed) {
            revert SwapLotteryErrors.InvalidWinnerNumber();
        }

        if (lottery.prizePool == 0) {
            revert SwapLotteryErrors.InsufficientFunds();
        }

        uint256 prizeAmount = lottery.prizePool;
        lottery.prizePool = 0;
        lottery.prizeClaimed = true;

        (bool success, ) = payable(msg.sender).call{value: prizeAmount}("");
        require(success, "Transfer failed");

        emit PrizeClaimed(_lotteryId, msg.sender, prizeAmount);
    }

    /**
     * @dev Add funds to current lottery prize pool
     */
    function addToPrizePool() external payable {
        if (msg.value > 0) {
            Lottery storage lottery = lotteries[currentLotteryId];
            lottery.prizePool += msg.value;
        }
    }

    /**
     * @dev Get lottery information
     */
    function getLottery(uint256 _lotteryId) external view lotteryExists(_lotteryId) returns (
        uint256 lotteryId,
        uint256 startTime,
        uint256 endTime,
        uint256 prizePool,
        uint256 participantCount,
        address winner,
        uint256 winningNumber,
        bool isActive,
        bool isEnded,
        bool randomNumberReceived,
        uint64 sequenceNumber
    ) {
        Lottery storage lottery = lotteries[_lotteryId];
        return (
            lottery.lotteryId,
            lottery.startTime,
            lottery.endTime,
            lottery.prizePool,
            lottery.participants.length,
            lottery.winner,
            lottery.winningNumber,
            lottery.isActive,
            lottery.isEnded,
            lottery.randomNumberReceived,
            lottery.sequenceNumber
        );
    }

    /**
     * @dev Get lottery participants
     */
    function getLotteryParticipants(uint256 _lotteryId) external view lotteryExists(_lotteryId) returns (address[] memory) {
        return lotteries[_lotteryId].participants;
    }

    /**
     * @dev Get user's ticket numbers for a lottery
     */
    function getUserTickets(uint256 _lotteryId, address user) external view lotteryExists(_lotteryId) returns (uint256[] memory) {
        return lotteries[_lotteryId].userTickets[user];
    }

    /**
     * @dev Get user statistics
     */
    function getUserStats(address _user) external view returns (
        uint256 totalWinnings,
        uint256 participationCount
    ) {
        return (userTotalWinnings[_user], userParticipationCount[_user]);
    }

    /**
     * @dev Get contract statistics
     */
    function getContractStats() external view returns (
        uint256 totalLotteries_,
        uint256 totalPrizesDistributed_,
        uint256 currentLotteryId_,
        uint256 currentPrizePool,
        uint256 lotteryDuration_,
        uint256 minParticipants_
    ) {
        return (
            totalLotteries,
            totalPrizesDistributed,
            currentLotteryId,
            lotteries[currentLotteryId].prizePool,
            lotteryDuration,
            minParticipants
        );
    }

    /**
     * @dev Get entropy fee required for random number generation
     */
    function getEntropyFee() public view returns (uint256) {
        return entropy.getFeeV2();
    }

    /**
     * @dev Required by IEntropyConsumer interface
     */
    function getEntropy() internal view override returns (address) {
        return address(entropy);
    }

    /**
     * @dev Update lottery settings (owner only)
     */
    function updateLotterySettings(uint256 _lotteryDuration, uint256 _minParticipants) external onlyOwner {
        if (_lotteryDuration < 1 hours || _lotteryDuration > 30 days) {
            revert SwapLotteryErrors.InvalidLotteryDuration();
        }

        lotteryDuration = _lotteryDuration;
        minParticipants = _minParticipants;
    }

    /**
     * @dev Update entropy provider (owner only)
     */
    function setEntropyProvider(address _newProvider) external onlyOwner {
        entropyProvider = _newProvider;
    }

    /**
     * @dev Emergency functions (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        payable(owner).transfer(address(this).balance);
    }

    /**
     * @dev Transfer ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid new owner");
        owner = newOwner;
    }

    /**
     * @dev Accept HBAR deposits
     */
    receive() external payable {
        if (msg.value > 0) {
            Lottery storage lottery = lotteries[currentLotteryId];
            lottery.prizePool += msg.value;
        }
    }
}

